configfiles.configfile:
@init_logger@
import onnx import torch from ms2deepscore import MS2DeepScore from matchms import calculate_scores from matchms.importing import load_from_msp, scores_from_json from ms2deepscore.models.SiameseSpectralModel import SiameseSpectralModel from ms2deepscore.SettingsMS2Deepscore import SettingsMS2Deepscore
onnx_model = onnx.load("$model")
# Extract the initializers (weights and biases) initializers = {init.name: onnx.numpy_helper.to_array(init) for init in onnx_model.graph.initializer}
# Convert NumPy arrays to PyTorch tensors state_dict = {name: torch.tensor(np_array) for name, np_array in initializers.items()}
@json_load@
model = SiameseSpectralModel(settings=SettingsMS2Deepscore(**model_params)) model.load_state_dict(state_dict) model.eval()
#if $scores.use_scores == "True" scores_in = scores_from_json("${scores_in}") references=scores_in.references queries=scores_in.queries #else references=list(load_from_msp("$references")) queries=list(load_from_msp("$queries")) #end if
similarity = MS2DeepScore(model) scores = calculate_scores(references, queries, similarity)
#if $scores.use_scores == "True" scores_in._scores.add_dense_matrix(scores.to_array(), "MS2DeepScore") scores_in.to_json("$similarity_scores") #else scores.to_json("$similarity_scores") #end if
